func countPublishedArticles(articles []models.Article) int {
	count := 0
	for _, article := range articles {
		if article.IsPublished {
			count++
		}
	}
	return count
}

func countDraftArticles(articles []models.Article) int {
	count := 0
	for _, article := range articles {
		if !article.IsPublished {
			count++
		}
	}
	return count
}

